import Foundation
import Security


class CertificateEncrypted {

    
    

    
    static func encryptedPublicKey() -> SecKey {
        var error: Unmanaged<CFError>?

        let pubicKeyPem = "".replace(string: "\r", replacement: "").replace(string: "\n", replacement: "")
        
        let attributesRSAPub: [String:Any] = [
            kSecAttrKeyType as String: kSecAttrKeyTypeRSA,
            kSecAttrKeyClass as String: kSecAttrKeyClassPublic,
            kSecAttrKeySizeInBits as String: 2048,
            kSecAttrIsPermanent as String: false
        ]

        let pubKeyRSAData = Data(base64Encoded: pubicKeyPem, options: Data.Base64DecodingOptions.ignoreUnknownCharacters)
        let publicKeySec = SecKeyCreateWithData(pubKeyRSAData as! CFData, attributesRSAPub as CFDictionary, &error)
        return publicKeySec!
    }
    
    
}
