    static let prod: Self = .init(
        edges: .init(top: 132, leading: 16, bottom: 0, trailing: 16),
        footer: .splashScreenName,
        footerVPadding: 48,
        logo: .splashScreenLogo,
        logoSize: 64,
        spacing: 32,
        subtextFont: .textH3R18240(),
        textFont: .textH1Sb24322()
