}

private extension Image {
    
    static let splashScreenLogo = svg(svgString: .logo)
    static let splashScreenName = svg(svgString: .name)
    
    static func svg(
        svgString: String
    ) -> Image {
        
        guard let image = Image(svg: svgString, retry: 1) else {
            
            print("Can't create image \"\(svgString)\"")
            return .init("placeholder")
        }
        
        return image
    }
}

